var p5={};p5.sq=function(a){return a*a;};p5.constrain=function(amt,low,high){return(amt<low)?low:((amt>high)?high:amt);};p5.degrees=function(radians){return radians*(180/Math.PI);};p5.mag=function(a,b){return Math.sqrt(a*a+b*b);};p5.dist=function(x1,y1,x2,y2){return Math.sqrt(p5.sq(x2-x1)+p5.sq(y2-y1));};p5.lerp=function(start,stop,amt){return start+(stop-start)*amt;};p5.norm=function(value,start,stop){return(value-start)/(stop-start);};p5.map=function(value,istart,istop,ostart,ostop){return ostart+(ostop-ostart)*((value-istart)/(istop-istart));}
